/* eslint-disable react/react-in-jsx-scope */
import gql from 'graphql-tag';
import { useQuery } from '@apollo/react-hooks';
import {
  Card, ResourceList, Stack, TextStyle, Thumbnail,
} from '@shopify/polaris';
import store from 'store-js';

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;

function ProductList() {
  const { loading, error, data } = useQuery(GET_PRODUCTS_BY_ID, { variables: { ids: store.get('ids') } }, { ssr: false });
  const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error.message}</div>;

  console.log(data);

  return (
    <Card>
      <ResourceList
        showHeader
        resourceName={{ singular: 'Product', plural: 'Products' }}
        items={data.nodes}
        renderItem={(item) => {
          const media = (
            <Thumbnail
              source={
                        item.images.edges[0]
                          ? item.images.edges[0].node.originalSrc
                          : ''
                      }
              alt={
                        item.images.edges[0]
                          ? item.images.edges[0].node.altText
                          : ''
                      }
            />
          );
          const { price } = item.variants.edges[0].node;
          return (
            <ResourceList.Item
              id={item.id}
              media={media}
              accessibilityLabel={`View details for ${item.title}`}
            >
              <Stack>
                <Stack.Item fill>
                  <h3>
                    <TextStyle variation="strong">
                      {item.title}
                    </TextStyle>
                  </h3>
                </Stack.Item>
                <Stack.Item>
                  <p>
                    $
                    {price}
                  </p>
                </Stack.Item>
                <Stack.Item>
                  <p>
                    Expires on
                    {twoWeeksFromNow}
                  </p>
                </Stack.Item>
              </Stack>
            </ResourceList.Item>
          );
        }}
      />
    </Card>
  );
}

export default ProductList;
